import React, {useState, useEffect } from 'react';
import { fetchDailyData } from '../../api';
import { Line, Bar } from 'react-chartjs-2';

import styles from './Chart.module.css'


const Chart = () => {
    const [dailyData, setDailyData] = useState({});

    useEffect(() => { //use affect is being used with a callback function inside (fetchAPI)
        const fetchAPI = async () => {
            setDailyData (await fetchDailyData()); //fetchDailyData comes from our api
        }

        console.log(dailyData);

        fetchAPI();
    });


    const lineChart = (
        //using ternary operator like an if, if no data in index 0 of dailydata, returns null, if not go to line chart
        dailyData[0] 
        ? (<Line 
        data = {{
            labels: '',
            datasets: [{}, {}],
        }}
        />) : null
    );


    return (
        <h1>
            Chart
        </h1>
    )
}



export default Chart;
